mapper.select:
select cp.t_param_id AS id, cp.t_param_name AS name, cp.t_special_param AS specialParam, cp.t_create_time AS createTime, cp.t_update_time AS updateTime, ca.t_attribute_name AS attributeName from codeworld_param AS cp left join codeworld_attribute AS ca on cp.t_attribute_id = ca.t_attribute_id
and cp.t_param_name = #{name}
and cp.t_create_time >= #{createTime}
and cp.t_update_time <= #{updateTime}
and cp.t_attribute_id = #{attributeId}
order by cp.t_create_time DESC

select cp.t_param_id AS id, cp.t_param_name AS name from codeworld_param AS cp where cp.t_attribute_id in
#{attributeId}
and cp.t_special_param = 0

select cp.t_param_id AS id, cp.t_param_name AS name from codeworld_param AS cp where cp.t_attribute_id in
#{attributeId}
and cp.t_special_param = 1
