mapper.select:
select cp.t_product_id AS id, cp.t_product_title AS title, cp.t_product_category_id AS categoryId, cp.t_product_sub_title AS subTitle, cp.t_product_saleable AS saleAble, cp.t_product_create_time AS createTime, cp.t_product_update_time AS updateTime, cp.t_product_merchant_id AS merchantId, cp.t_product_approve_status AS approveStatus, cp.t_product_approve_remark AS approveRemark, cc.t_category_name AS categoryName, cpd.t_product_view AS view, cp.t_store_id AS storeId from codeworld_product AS cp left join codeworld_category AS cc on cp.t_product_category_id = cc.t_category_id left join codeworld_product_detail AS cpd on cp.t_product_id = cpd.t_product_id
and t_product_title like "%"#{title}"%"
and t_product_saleable = #{saleAble}
and cp.t_product_create_time >= #{createTime}
and cp.t_product_update_time <= #{updateTime}
and t_product_merchant_id = #{merchantId}
and cp.t_product_saleable != -1
order by cp.t_product_create_time DESC

mapper.update:
update codeworld_product
set
t_product_saleable = #{saleAble},
t_product_update_time = #{updateTime}
where t_product_id = #{id}

update codeworld_product
set
t_product_approve_status = #{approveStatus},
t_product_approve_remark = #{approveRemark}
where
t_product_id = #{id}
