mapper.select:
select t_carouse_id, t_carouse_title, t_carouse_image, t_carouse_status, t_carouse_start_time, t_carouse_end_time, t_carouse_review_status, t_carouse_position from codeworld_carouse
and t_carouse_status = #{status}
and t_carouse_start_time >= #{startTime}
and t_carouse_end_time <= #{endTime}
and t_merchant_number = #{merchantNumber}
order by t_carouse_start_time DESC

select t_carouse_id, t_carouse_title, t_carouse_image, t_carouse_status, t_carouse_start_time, t_carouse_end_time, t_carouse_review_status, t_carouse_position from codeworld_carouse
and t_carouse_status = #{status}
and t_carouse_start_time >= #{startTime}
and t_carouse_end_time <= #{endTime}
order by t_carouse_start_time DESC

mapper.update:
update
codeworld_carouse
set
t_carouse_status = #{status}
where
t_carouse_id = #{id}
