mapper.select:
select cm.t_merchant_id AS id, cm.t_merchant_number AS number, cm.t_merchant_nick_name AS nickName, cm.t_merchant_phone AS phone, cm.t_merchant_create_time AS createTime, cmd.t_merchant_status AS status, cmd.t_merchant_name AS merchantName, cmd.t_merchant_id_card_front AS idCardFront, cmd.t_merchant_id_card_back AS idCardBack, cmd.t_merchant_license AS license, cmd.t_merchant_type AS type, cmd.t_merchant_set_create_time AS setCreateTime from codeworld_merchant AS cm left join codeworld_merchant_detail As cmd on cmd.t_merchant_number = cm.t_merchant_number
and cm.t_merchant_nick_name like "%"#{nickName}"%"
and cm.t_merchant_create_time >= #{createTime}
and cm.t_merchant_create_time <= #{updateTime}
and cmd.t_merchant_set_create_time <= #{settlementTime}
and t_merchant_follow_user = #{merchantFollowUser}
order by cm.t_merchant_create_time DESC

mapper.update:
update
codeworld_merchant
set
t_merchant_follow_user = #{merchantFollowUser}
where
t_merchant_number = #{number}

update
codeworld_merchant
set
t_merchant_password = #{password}
where
t_merchant_number = #{number}
