mapper.select:
select co.t_order_id AS orderId, co.t_order_total_pay AS totalPay, co.t_order_address_id AS addressId, co.t_order_create_time AS createTime, cos.t_order_status AS orderStatus, cor.t_return_order_status AS orderReturnStatus from codeworld_order AS co left join codeworld_order_detail AS cod on co.t_order_id = cod.t_order_id left join codeworld_order_status AS cos on cod.t_order_detail_id = cos.t_order_id left join codeworld_order_return AS cor on cod.t_order_detail_id = cor.t_order_id
and cos.t_order_status = #{orderStatus}
and co.t_order_member_id = #{memberId}
order by co.t_order_create_time DESC

select co.t_order_id AS orderId, co.t_order_address_id AS addressId, co.t_order_create_time AS createTime, co.t_order_member_id AS memberId, co.t_order_buyer_name AS buyerName, cos.t_order_status AS orderStatus from codeworld_order_detail AS cod inner join codeworld_order AS co on cod.t_order_id = co.t_order_id inner join codeworld_order_status AS cos on cos.t_order_id = co.t_order_id
and cos.t_order_status = #{orderStatus}
and cod.t_merchant_number = #{merchantNumber}
order by co.t_order_create_time DESC

select co.t_order_id AS orderId, co.t_order_address_id AS addressId, co.t_order_create_time AS createTime, co.t_order_member_id AS memberId, co.t_order_buyer_name AS buyerName, co.t_order_total_pay AS totalPay, cos.t_order_status AS orderStatus from codeworld_order AS co inner join codeworld_order_detail AS cod on cod.t_order_id = co.t_order_id inner join codeworld_order_status AS cos on cos.t_order_id = co.t_order_id
cos.t_order_status = #{orderStatus}
and cod.t_merchant_number = #{merchantNumber}
and co.t_order_id = #{orderId}
and co.t_order_create_time == #{createTime}
group by co.t_order_id order by co.t_order_create_time DESC

mapper.update:
update
codeworld_order
set
t_order_actual_pay = #{actualPay}
where
t_order_id = #{id}
