mapper.select:
select cu.t_user_id AS userId, cu.t_user_name AS userName, cu.t_user_email AS userEmail, cu.t_user_phone AS userPhone, cu.t_user_status AS userStatus, cu.t_create_time AS createTime, cu.t_update_time AS updateTime, cr.t_role_name AS userRole, cd.t_dept_name AS userDept from codeworld_user AS cu left join codeworld_user_role AS cur on cur.t_user_id = cu.t_user_id left join codeworld_role AS cr on cr.t_role_id = cur.t_role_id left join codeworld_user_dept AS cud on cud.t_user_id = cu.t_user_id left join codeworld_dept AS cd on cd.t_dept_id = cud.t_dept_id
and cu.t_user_name like "%"#{userName}"%"
and cu.t_user_email like "%"#{userEmail}"%"
and cu.t_user_phone like "%"#{userPhone}"%"
and cu.t_user_status = #{userStatus}
and cu.t_create_time >= #{createTime}
and cu.t_update_time <= #{updateTime}
order by cu.t_create_time DESC

mapper.update:
update codeworld_user
set
t_user_status = #{userStatus}
where t_user_id = #{userId}
